import React from 'react';
import './App.css'; // Import your custom CSS

function TodoList({ tasks, toggleTask, deleteTask, startEditing, editTask }) {
  return (
    <ul className="list-group">
      {tasks.map((task, index) => (
        <li
          key={index}
          className={`list-group-item d-flex justify-content-between align-items-center task-item ${task.completed ? 'task-completed' : 'task-incomplete'}`}
          style={{ position: 'relative' }}
        >
          <div>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => toggleTask(index)}
              className="me-2"
            />
            {task.isEditing ? (
              <input
                type="text"
                value={task.name}
                onChange={(e) => editTask(e, index)}
                className="form-control"
                style={{ display: 'inline-block', width: 'auto' }}
              />
            ) : (
              <span style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
                {task.name}
              </span>
            )}
          </div>
          <div>
            {task.isEditing ? (
              <button onClick={() => startEditing(index)} className="btn btn-success btn-sm me-2">
                Save
              </button>
            ) : (
              <button onClick={() => startEditing(index)} className="btn btn-primary btn-sm me-2">
                Edit
              </button>
            )}
            <button onClick={() => deleteTask(index)} className="btn btn-danger btn-sm">
              Delete
            </button>
          </div>
          {!task.completed && (
            <div className="tooltip">
              This task is incomplete
            </div>
          )}
        </li>
      ))}
    </ul>
  );
}

export default TodoList;
